import React from "react";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { motion } from "framer-motion";
import "./Introducao.css"; // Estilos adicionais

const Introducao = () => {
  return (
    <div className="introducao-container">
      <Parallax pages={2}>
        {/* Fundo Paralaxe */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          style={{
            backgroundImage: "url('/path-to-your-background-image.jpg')",
            backgroundSize: "cover",
          }}
        />

        {/* Camada de Conteúdo */}
        <ParallaxLayer offset={0.2} speed={1}>
          <div className="introducao-content">
            <motion.h1
              initial={{ opacity: 0, y: -50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1 }}
              className="introducao-title"
            >
              Bem-vindo ao Cilada
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, delay: 0.5 }}
              className="introducao-description"
            >
              Explore ameaças cibernéticas e aprenda como se proteger.
            </motion.p>
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              className="btn-cta"
            >
              Explorar Ameaças
            </motion.button>
          </div>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default Introducao;
