import React from "react";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { motion } from "framer-motion";
import background from "../../assets/background5.jpg";
import golpe1 from "../../assets/golpe1.png";
import golpe2 from "../../assets/golpe2.png";
import golpe3 from "../../assets/golpe3.png";
import "./Parallax.css";

const golpes = [
  {
    id: 1,
    imagem: golpe1,
    titulo: "Phishing",
    descricao: "Tentativas de obter informações confidenciais, como senhas e dados bancários.",
  },
  {
    id: 2,
    imagem: golpe2,
    titulo: "Ransomware",
    descricao: "Malware que sequestra seus dados e exige resgate.",
  },
  {
    id: 3,
    imagem: golpe3,
    titulo: "Spyware",
    descricao: "Software espião que coleta informações sem o seu consentimento.",
  },
];

const ParallaxEffect = () => {
  return (
    <div className="parallax-container">
      <Parallax pages={2}>
        {/* Primeira Sessão */}
        <ParallaxLayer
          offset={0}
          speed={2}
          style={{
            backgroundImage: `url(${background})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        />
        <ParallaxLayer offset={0.3} speed={1}>
          <div className="parallax-content">
            <motion.h1
              initial={{ opacity: 0, y: -50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1 }}
            >
              Proteja Seu Futuro Digital
            </motion.h1>
            <motion.p
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, delay: 0.5 }}
            >
              Descubra como podemos ajudar você a navegar com segurança.
            </motion.p>
          </div>
        </ParallaxLayer>

        {/* Segunda Sessão - Principais Golpes */}
        <ParallaxLayer
          offset={1}
          speed={0.5}
          style={{
            backgroundColor: "#f5f5f5",
          }}
        >
          <div className="principais-golpes-container">
            <motion.h2
              className="principais-golpes-titulo"
              initial={{ opacity: 0, y: -30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, delay: 0.2 }}
            >
              Principais Golpes Cibernéticos
            </motion.h2>
            <div className="principais-golpes-grid">
              {golpes.map((golpe) => (
                <motion.div
                  key={golpe.id}
                  className="golpe-card"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                  transition={{ duration: 0.5, delay: golpe.id * 0.2 }}
                >
                  <img
                    src={golpe.imagem}
                    alt={golpe.titulo}
                    className="golpe-imagem"
                  />
                  <h3 className="golpe-titulo">{golpe.titulo}</h3>
                  <p className="golpe-descricao">{golpe.descricao}</p>
                </motion.div>
              ))}
            </div>
          </div>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
};

export default ParallaxEffect;
