import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import "./Noticias.css";

const Noticias = () => {
  const [noticias, setNoticias] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchNoticias = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/noticias"); // Backend
        setNoticias(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Erro ao buscar notícias:", error);
        setLoading(false);
      }
    };

    fetchNoticias();
  }, []);

  return (
    <motion.section
      className="noticias-container"
      initial={{ opacity: 0, y: 50 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 1 }}
      viewport={{ once: true }}
      id="noticias"
    >
      <motion.h2
        className="noticias-titulo"
        initial={{ opacity: 0, y: -30 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 0.2 }}
      >
        Últimas Notícias sobre Segurança Cibernética
      </motion.h2>
      {loading ? (
        <p className="loading-text">Carregando notícias...</p>
      ) : (
        <div className="noticias-grid">
          {noticias.slice(0, 6).map((noticia, index) => (
            <motion.div
              key={index}
              className="noticia-card"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
            >
              <img
                src={noticia.urlToImage || "/placeholder.jpg"}
                alt={noticia.title}
                className="noticia-imagem"
              />
              <h3 className="noticia-titulo">{noticia.title}</h3>
              <p className="noticia-descricao">
                {noticia.description || "Sem descrição disponível"}
              </p>
              <a
                href={noticia.url}
                target="_blank"
                rel="noopener noreferrer"
                className="noticia-link"
              >
                Ler Mais
              </a>
            </motion.div>
          ))}
        </div>
      )}
    </motion.section>
  );
};

export default Noticias;
